function d2h(d) {
	var h = d.toString(16);
	if (d < 16)
		return '0'+h;
	return h;
	}
function h2d(h) {return parseInt(h,16);}

function rgb2hex(r, g, b)
{
	var lastHex = d2h(r)+d2h(g)+d2h(b);

	return lastHex;
}


